import React, { useEffect, useState}  from "react";
import "./NavBar.css";
import metadata from "../data/metadata.json";
import { useNavigate } from 'react-router-dom';

const NavBar = () => {
    const [token, setToken] = useState('')
  const navigate = useNavigate();

  function logOut() {
    setToken('')
    localStorage.setItem('jwtToken', '');
  }

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if(token) {
      setToken(token)
    }
  },[token])
    return (
        <div className="navbar">
            {token != '' ? (
          <>
          <li className="nav-item">
          <a className="nav-links" href="/audio">AudioHub</a>
        </li>
        <li className="nav-item">
          <a className="nav-links" href="/login" onClick={logOut}>Logout</a>
        </li>
          </>
        ) : (
          <>
          <li className="nav-item">
          <a className="nav-links" href="/login">Login</a>
        </li>
        <li className="nav-item">
          <a className="nav-links" href="/signup">SignUp</a>
        </li>
          </>
        )}
            <i className="fab fa-spotify"></i>
            <div className="app-header">{metadata.appName}</div>
            <div className="nav-links">


            </div>
        </div>
    );
};

export default NavBar;
