import jwt from 'jsonwebtoken';

const generateToken = (user) => {
    const token = jwt.sign(
      { id: user.id, email: user.email },
      'shhhhhh1',
      { expiresIn: '1h' } // optional: set the expiration time
    );
    return token;
  };
